// ---------------------------------------------------------------------------
// Seed function
// ---------------------------------------------------------------------------

async function seed() {
  console.log("üå± Starting seed...\n");

  // ------ Clear existing data (respect FK order) ------
  console.log("Clearing existing data...");
  await db.delete(impressions);
  console.log("  ‚úì Cleared impressions");
  await db.delete(votes);
  console.log("  ‚úì Cleared votes");
  await db.delete(options);
  console.log("  ‚úì Cleared options");
  await db.delete(questions);
  console.log("  ‚úì Cleared questions");
  await db.delete(categories);
  console.log("  ‚úì Cleared categories");
  console.log("");

  // ------ Insert categories ------
  console.log("Inserting categories...");
  const insertedCategories = await db
    .insert(categories)
    .values(categoryData)
    .returning({ id: categories.id, slug: categories.slug });

  const categoryMap = new Map<string, string>();
  for (const cat of insertedCategories) {
    categoryMap.set(cat.slug, cat.id);
  }
  console.log(`  ‚úì Inserted ${insertedCategories.length} categories\n`);

  // ------ Insert questions & options ------
  let totalQuestions = 0;
  let totalOptions = 0;

  for (const [slug, qs] of Object.entries(questionData)) {
    const categoryId = categoryMap.get(slug);
    if (!categoryId) {
      console.error(`  ‚úó Category "${slug}" not found ‚Äî skipping`);
      continue;
    }

    console.log(`Inserting questions for "${slug}"...`);

    for (const q of qs) {
      const [insertedQuestion] = await db
        .insert(questions)
        .values({
          categoryId,
          prompt: q.prompt,
          subtitle: q.subtitle ?? null,
          tags: q.tags ?? null,
          status: "active",
        })
        .returning({ id: questions.id });

      totalQuestions++;

      const optionValues = q.options.map((name, idx) => ({
        questionId: insertedQuestion.id,
        name,
        sortOrder: idx,
      }));

      await db.insert(options).values(optionValues);
      totalOptions += optionValues.length;
    }

    console.log(`  ‚úì ${qs.length} questions inserted`);
  }

  console.log(`\nüéâ Seed complete!`);
  console.log(`   ${insertedCategories.length} categories`);
  console.log(`   ${totalQuestions} questions`);
  console.log(`   ${totalOptions} options`);
}

// ---------------------------------------------------------------------------
// Run
// ---------------------------------------------------------------------------

seed()
  .then(() => {
    process.exit(0);
  })
  .catch((err) => {
    console.error("Seed failed:", err);
    process.exit(1);
  });
